import React, { useEffect, useState } from "react";

const LOCAL_STORAGE_KEY = "portfolio-added-certifications";

export default function Certifications() {
  const baseCertifications = [
    {
      title: "Automation Developer Associate (UiPath)",
      description: "Certified by UiPath Academy"
    },
    {
      title: "React.js Crash Course â€“ Udemy",
      description: "Completed a 6-hour beginner course on React"
    },
    {
      title: "Intro to Industry 4.0 and IoT â€“ NPTEL",
      description: "Fundamentals of IoT in smart manufacturing"
    },
    {
      title: "Intro to Machine Learning â€“ Great Learning",
      description: "Overview of supervised and unsupervised learning"
    }
  ];

  const [addedCerts, setAddedCerts] = useState([]);
  const [newCert, setNewCert] = useState({ title: "", description: "" });

  useEffect(() => {
    const stored = localStorage.getItem(LOCAL_STORAGE_KEY);
    if (stored) {
      setAddedCerts(JSON.parse(stored));
    }
  }, []);

  const handleAddCert = () => {
    if (newCert.title.trim() !== "" && newCert.description.trim() !== "") {
      const updated = [...addedCerts, { ...newCert }];
      setAddedCerts(updated);
      localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(updated));
      setNewCert({ title: "", description: "" });
    } else {
      alert("Please fill in both the title and description.");
    }
  };

  const handleDeleteCert = (indexToDelete) => {
    const updated = addedCerts.filter((_, index) => index !== indexToDelete);
    setAddedCerts(updated);
    localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(updated));
  };

  return (
    <section className="max-w-4xl mx-auto mb-10 p-6 bg-white dark:bg-gray-800 rounded-xl shadow-md">
      <h2 className="text-3xl font-semibold border-b pb-2 mb-4 text-purple-600 dark:text-purple-300">
        Certifications
      </h2>

      {/* Add Certification Form */}
      <div className="grid md:grid-cols-2 gap-4 mb-6">
        <input
          type="text"
          placeholder="Certificate Title"
          className="border px-3 py-2 rounded"
          value={newCert.title}
          onChange={(e) => setNewCert({ ...newCert, title: e.target.value })}
        />
        <input
          type="text"
          placeholder="Short Description"
          className="border px-3 py-2 rounded"
          value={newCert.description}
          onChange={(e) => setNewCert({ ...newCert, description: e.target.value })}
        />
      </div>

      <div className="mb-6 text-right">
        <button
          onClick={handleAddCert}
          className="bg-purple-600 text-white px-4 py-2 rounded hover:bg-purple-700 transition"
        >
          âž• Add Certification
        </button>
      </div>

      <ul className="space-y-4">
        {[...baseCertifications, ...addedCerts].map((cert, index) => {
          const isUserAdded = index >= baseCertifications.length;
          return (
            <li
              key={index}
              className="list-item bg-gray-100 dark:bg-gray-700 p-4 rounded-lg shadow-sm flex justify-between items-start"
            >
              <div>
                <p className="font-semibold text-blue-700 dark:text-blue-300">
                  {cert.title}
                </p>
                <p className="text-sm text-gray-600 dark:text-gray-300 mt-1">
                  {cert.description}
                </p>
              </div>
              {isUserAdded && (
                <button
                  onClick={() => handleDeleteCert(index - baseCertifications.length)}
                  className="text-red-500 hover:text-red-700 ml-4 text-sm"
                  title="Delete certification"
                >
                  ðŸ—‘
                </button>
              )}
            </li>
          );
        })}
      </ul>
    </section>
  );
}
