import React from "react";
import { motion } from "framer-motion";

export default function Hero() {
  return (
    <motion.section
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 1 }}
      className="flex flex-col items-center justify-center text-center min-h-[70vh] px-4 bg-gradient-to-b from-purple-500 via-indigo-500 to-blue-500 text-white"
    >
      {/* Name Animation */}
      <motion.h1
        className="text-4xl sm:text-5xl md:text-6xl font-bold mb-4"
        initial={{ y: -50, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 0.8 }}
      >
        Hi, I'm Nigashini{" "}
        <motion.span
          initial={{ rotate: 0 }}
          animate={{ rotate: [0, 20, -10, 20, -10, 0] }}
          transition={{ duration: 1.2, repeat: Infinity, repeatDelay: 4 }}
          className="inline-block origin-bottom"
        >
          ðŸ‘‹
        </motion.span>
      </motion.h1>

      {/* Subtitle Animation */}
      <motion.p
        className="text-lg md:text-xl max-w-xl mb-6"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1.2, delay: 0.5 }}
      >
        A passionate IT student focused on building beautiful, functional websites and solving real-world problems with code.
      </motion.p>

      {/* Resume Button Animation */}
      <motion.a
        href="/Nigashini_Resume.pdf"
        download
        className="px-6 py-3 bg-white text-purple-700 font-semibold rounded-lg shadow-md hover:bg-purple-100 transition"
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.95 }}
        initial={{ opacity: 0, y: 10 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1, delay: 1 }}
      >
        View my Resume
      </motion.a>
    </motion.section>
  );
}
