import React, { useState } from "react";
import { motion } from "framer-motion";

export default function Projects() {
  const [projects, setProjects] = useState([
    {
      title: "Student Attendance System",
      description: "React-based UI with API integration.",
      github: "https://github.com/your-username/attendance-system",
      image: "https://img.icons8.com/external-flatart-icons-outline-flatarticons/64/code.png"
    }
    // add more...
  ]);

  const [newProject, setNewProject] = useState({ title: "", description: "", github: "", image: "" });

  const handleAdd = () => {
    if (newProject.title && newProject.description && newProject.github) {
      setProjects([...projects, newProject]);
      setNewProject({ title: "", description: "", github: "", image: "" });
    }
  };

  return (
    <section className="max-w-6xl mx-auto p-6">
      <h2 className="text-3xl font-bold mb-6 text-center">Projects</h2>

      {/* ðŸ’¡ Add Project Form */}
      <div className="mb-6 grid gap-2 md:grid-cols-2 lg:grid-cols-4">
        <input
          className="border rounded px-2 py-1"
          placeholder="Title"
          value={newProject.title}
          onChange={(e) => setNewProject({ ...newProject, title: e.target.value })}
        />
        <input
          className="border rounded px-2 py-1"
          placeholder="Description"
          value={newProject.description}
          onChange={(e) => setNewProject({ ...newProject, description: e.target.value })}
        />
        <input
          className="border rounded px-2 py-1"
          placeholder="GitHub URL"
          value={newProject.github}
          onChange={(e) => setNewProject({ ...newProject, github: e.target.value })}
        />
        <input
          className="border rounded px-2 py-1"
          placeholder="Image URL"
          value={newProject.image}
          onChange={(e) => setNewProject({ ...newProject, image: e.target.value })}
        />
      </div>
      <button
        onClick={handleAdd}
        className="mb-8 px-4 py-2 bg-purple-600 text-white rounded hover:bg-purple-700"
      >
        âž• Add Project
      </button>

      {/* ðŸ§± Projects Grid */}
      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
        {projects.map((project, index) => (
          <motion.div
            key={index}
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: index * 0.1 }}
            viewport={{ once: true }}
            className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow hover:scale-105 transition"
          >
            <img src={project.image} alt={project.title} className="w-10 h-10 mb-3" />
            <h3 className="text-xl font-bold">{project.title}</h3>
            <p className="text-sm mb-3">{project.description}</p>
            <a href={project.github} target="_blank" rel="noopener noreferrer" className="text-blue-500 underline">
              View on GitHub
            </a>
          </motion.div>
        ))}
      </div>
    </section>
  );
}
